import React, { useState } from 'react';
import ProjectForm from './components/ProjectForm';
import HardwareForm from './components/HardwareForm';
import PocForm from './components/PocForm';
import FormDataDisplay from './components/FormDataDisplay';
import './components/assets/css/App.css';
import LogoInovacode from './components/assets/logo/logo2.0.png';
import axios from 'axios';

function App() {
  const [formData, setFormData] = useState({
    name: 'Kaique',
    email: 'kaiquezeza@gmail.com',
    requestType: '',
    // Fields for Venda somente de hardwares
    companyName: 'Zeztecnology',
    cnpj: '509.562.588-16',
    vendorName: ' Kaique',
    totalValue: '10000000',
    observations: 'Venda do Zez Industrial',
    // Fields for Prova de conceito (POC)
    clientName: '',
    salesRepresentative: '',
    sponsor: '',
    requestedSolution: '',
    pocDate: '',
    applicationContext: '',
    chargeClient: '',
    // Attachments
    proposalAttachment: null,
    projectBidAttachment: null, // Only for Projeto Vendido
    contractAttachment: null, // Only if the project has a contract
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });

    // Specific handling for requestType
    if (name === 'requestType') {
      // Reset additional fields based on requestType
      setFormData((prevFormData) => {
        if (value === 'Venda somente de hardwares') {
          return { ...prevFormData, companyName: '', cnpj: '', vendorName: '', totalValue: '', observations: '' };
        } else if (value === 'Prova de conceito (POC)') {
          return { ...prevFormData, clientName: '', cnpj: '', salesRepresentative: '', sponsor: '', requestedSolution: '', pocDate: '', applicationContext: '', chargeClient: '' };
        } else {
          return prevFormData;
        }
      });
    }
  };

  const handleFileChange = (event) => {
    const { name, files } = event.target;
    setFormData({ ...formData, [name]: files[0] });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Dados do formulário enviados:', formData);

    // Criar um objeto FormData para enviar arquivos
    const formDataToSend = new FormData();
    for (const key in formData) {
      formDataToSend.append(key, formData[key]);
    }

    // Enviar os dados do formulário para o backend
    axios.post('http://localhost:5000/api/send-email', formDataToSend)
      .then((response) => {
        console.log('Resposta do backend:', response.data);
        alert('Formulário enviado com sucesso! Verifique seu email.');
      })
      .catch((error) => {
        console.error('Erro ao enviar o formulário:', error);
        alert('Ocorreu um erro ao enviar o formulário. Tente novamente mais tarde.');
      });
  };

  const renderFormBasedOnRequestType = () => {
    switch (formData.requestType) {
      case 'Projeto Vendido':
      case 'Projeto PRO BONO (sem custo)':
      case 'Projeto Piloto':
        return <ProjectForm handleFileChange={handleFileChange} />;
      case 'Venda somente de hardwares':
        return <HardwareForm handleFileChange={handleFileChange} />;
      case 'Prova de conceito (POC)':
        return <PocForm handleFileChange={handleFileChange} />;
      default:
        return null;
    }
  };

  return (
    <div className="container-outer">
      <div className="App">
        <img src={LogoInovacode} alt="Inovacode Logo" width="150" height="60" />

        <h1>Formulário de Solicitação</h1>
        <form onSubmit={handleSubmit}>
          {/* ... Campos de entrada ... */}
          <button type="submit">Enviar</button>
        </form>
        {renderFormBasedOnRequestType()}
        <FormDataDisplay formData={formData} />
      </div>
    </div>
  );
}

export default App;
