import React, { useState } from 'react';
import ProjectForm from './components/ProjectForm';
import HardwareForm from './components/HardwareForm';
import PocForm from './components/PocForm';
import FormDataDisplay from './components/FormDataDisplay';
import './components/assets/css/App.css';
import LogoInovacode from './components/assets/logo/logo2.0.png';
import axios from 'axios';

function App() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    requestType: '',
  });

  const handleFileChange = (event) => {
    const { name, files } = event.target;
    setFormData({ ...formData, [name]: files[0] });
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });

    // Specific handling for requestType
    if (name === 'requestType') {
      // Reset additional fields based on requestType
      setFormData((prevFormData) => {
        if (value === 'Projeto Vendido') {
          return { ...prevFormData, companyName: '', cnpj: '', vendorName: '', totalValue: '', observations: '', pocDescription: '' };
        } else if (value === 'Venda somente de hardwares') {
          return { ...prevFormData, projectName: '', pocDescription: '', clientName: '', salesRepresentative: '', sponsor: '', requestedSolution: '', pocDate: '', applicationContext: '', chargeClient: '' };
        } else if (value === 'Prova de conceito (POC)') {
          return { ...prevFormData, projectName: '', companyName: '', cnpj: '', vendorName: '', totalValue: '', observations: '' };
        } else {
          return prevFormData;
        }
      });
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Dados do formulário enviados:', formData);

    // Enviar os dados do formulário para o backend
    const formDataToSend = new FormData();
    formDataToSend.append('name', formData.name);
    formDataToSend.append('email', formData.email);
    formDataToSend.append('requestType', formData.requestType);

    // Append additional fields based on requestType
    if (formData.requestType === 'Projeto Vendido') {
      formDataToSend.append('companyName', formData.companyName);
      formDataToSend.append('cnpj', formData.cnpj);
      formDataToSend.append('vendorName', formData.vendorName);
      formDataToSend.append('totalValue', formData.totalValue);
      formDataToSend.append('observations', formData.observations);
      formDataToSend.append('proposalAttachment', formData.proposalAttachment);
    } else if (formData.requestType === 'Venda somente de hardwares') {
      formDataToSend.append('companyName', formData.companyName);
      formDataToSend.append('cnpj', formData.cnpj);
      formDataToSend.append('vendorName', formData.vendorName);
      formDataToSend.append('totalValue', formData.totalValue);
      formDataToSend.append('observations', formData.observations);
      formDataToSend.append('proposalAttachment', formData.proposalAttachment);
    } else if (formData.requestType === 'Prova de conceito (POC)') {
      formDataToSend.append('clientName', formData.clientName);
      formDataToSend.append('cnpj', formData.cnpj);
      formDataToSend.append('salesRepresentative', formData.salesRepresentative);
      formDataToSend.append('sponsor', formData.sponsor);
      formDataToSend.append('requestedSolution', formData.requestedSolution);
      formDataToSend.append('pocDate', formData.pocDate);
      formDataToSend.append('applicationContext', formData.applicationContext);
      formDataToSend.append('chargeClient', formData.chargeClient);
      formDataToSend.append('proposalAttachment', formData.proposalAttachment);
    }

    axios.post('http://localhost:5000/api/send-email', formDataToSend)
      .then((response) => {
        console.log('Resposta do backend:', response.data);
        alert('Formulário enviado com sucesso! Verifique seu email.');
      })
      .catch((error) => {
        console.error('Erro ao enviar o formulário:', error);
        alert('Ocorreu um erro ao enviar o formulário. Tente novamente mais tarde.');
      });
  };

  const renderFormBasedOnRequestType = () => {
    switch (formData.requestType) {
      case 'Projeto Vendido':
      case 'Projeto PRO BONO (sem custo)':
      case 'Projeto Piloto':
        return <ProjectForm handleFileChange={handleFileChange} />;
      case 'Venda somente de hardwares':
        return <HardwareForm handleFileChange={handleFileChange} />;
      case 'Prova de conceito (POC)':
        return <PocForm handleFileChange={handleFileChange} />;
      default:
        return null;
    }
  };

  return (
    <div className="container-outer">
      <div className="App">
        <img src={LogoInovacode} alt="Inovacode Logo" width="150" height="60" />

        <h1>Formulário de Solicitação</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="name">Nome:</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label htmlFor="email">E-mail:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label htmlFor="requestType">Tipo de Solicitação:</label>
            <select
              id="requestType"
              name="requestType"
              value={formData.requestType}
              onChange={handleChange}
              required
            >
              <option value="">Selecione...</option>
              <option value="Projeto Vendido">Projeto Vendido</option>
              <option value="Venda somente de hardwares">Venda somente de hardwares</option>
              <option value="Prova de conceito (POC)">Prova de conceito (POC)</option>
              <option value="Projeto PRO BONO (sem custo)">Projeto PRO BONO (sem custo)</option>
              <option value="Projeto Piloto">Projeto Piloto</option>
            </select>
          </div>
          <button type="submit">Enviar</button>
        </form>
        {renderFormBasedOnRequestType()}
        <FormDataDisplay formData={formData} />
      </div>
    </div>
  );
}

export default App;
