import React, { useState } from 'react';
import './components/assets/css/App.css';
import LogoInovacode from './components/assets/logo/logo2.0.png';
import axios from 'axios';
import ProjectForm from './components/ProjectForm';
import HardwareForm from './components/HardwareForm';
import PocForm from './components/PocForm';
import FormDataDisplay from './components/FormDataDisplay';

function App() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    requestType: '',
    projectName: '', // Informações adicionais para Projeto Vendido
    companyName: '', // Informações adicionais para Venda somente de hardwares
    cnpj: '', // Informações adicionais para Venda somente de hardwares e Prova de conceito (POC)
    vendorName: '', // Informações adicionais para Venda somente de hardwares
    totalValue: '', // Informações adicionais para Venda somente de hardwares
    observations: '', // Informações adicionais para Venda somente de hardwares
    clientName: '', // Informações adicionais para Prova de conceito (POC)
    salesRepresentative: '', // Informações adicionais para Prova de conceito (POC)
    sponsor: '', // Informações adicionais para Prova de conceito (POC)
    requestedSolution: '', // Informações adicionais para Prova de conceito (POC)
    pocDate: '', // Informações adicionais para Prova de conceito (POC)
    applicationContext: '', // Informações adicionais para Prova de conceito (POC)
    chargeClient: '', // Informações adicionais para Prova de conceito (POC)
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });

    // Specific handling for requestType
    if (name === 'requestType') {
      // Reset additional fields based on requestType
      setFormData((prevFormData) => {
        if (value === 'Projeto Vendido') {
          return { ...prevFormData, projectName: '', companyName: '', cnpj: '', vendorName: '', totalValue: '', observations: '', clientName: '', salesRepresentative: '', sponsor: '', requestedSolution: '', pocDate: '', applicationContext: '', chargeClient: '' };
        } else if (value === 'Venda somente de hardwares') {
          return { ...prevFormData, projectName: '', clientName: '', salesRepresentative: '', sponsor: '', requestedSolution: '', pocDate: '', applicationContext: '', chargeClient: '' };
        } else if (value === 'Prova de conceito (POC)') {
          return { ...prevFormData, projectName: '', companyName: '', vendorName: '', totalValue: '', observations: '' };
        } else {
          return prevFormData;
        }
      });
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Dados do formulário enviados:', formData);

    // Enviar os dados do formulário para o backend
    axios.post('http://localhost:5000/api/send-email', formData)
      .then((response) => {
        console.log('Resposta do backend:', response.data);
        alert('Formulário enviado com sucesso! Verifique seu email.');
      })
      .catch((error) => {
        console.error('Erro ao enviar o formulário:', error);
        alert('Ocorreu um erro ao enviar o formulário. Tente novamente mais tarde.');
      });
  };

  const renderFormBasedOnRequestType = () => {
    switch (formData.requestType) {
      case 'Projeto Vendido':
        return <ProjectForm handleChange={handleChange} formData={formData} />;
      case 'Venda somente de hardwares':
        return <HardwareForm handleChange={handleChange} formData={formData} />;
      case 'Prova de conceito (POC)':
        return <PocForm handleChange={handleChange} formData={formData} />;
      default:
        return null;
    }
  };

  return (
    <div className="container-outer">
      <div className="App">
        <img src={LogoInovacode} alt="Inovacode Logo" width="150" height="60" />

        <h1>Formulário de Solicitação</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="name">Nome:</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label htmlFor="email">E-mail:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label htmlFor="requestType">Tipo de Solicitação:</label>
            <select
              id="requestType"
              name="requestType"
              value={formData.requestType}
              onChange={handleChange}
              required
            >
              <option value="">Selecione...</option>
              <option value="Projeto Vendido">Projeto Vendido</option>
              <option value="Venda somente de hardwares">Venda somente de hardwares</option>
              <option value="Prova de conceito (POC)">Prova de conceito (POC)</option>
            </select>
          </div>
          <button type="submit">Enviar</button>
        </form>
        <FormDataDisplay formData={formData} />
        {renderFormBasedOnRequestType()}
      </div>
    </div>
  );
}

export default App;
