import React, { useState } from 'react';
import './assets/css/App.css';
import axios from 'axios';

const HardwareForm = () => {
  const [formData, setFormData] = useState({
    companyName: '',
    cnpj: '',
    vendorName: '',
    totalValue: '',
    observations: '',
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Dados do formulário enviados:', formData);

    // Enviar os dados do formulário para o backend
    axios
      .post('http://localhost:5000/api/send-email', formData)
      .then((response) => {
        console.log('Resposta do backend:', response.data);
        alert('Formulário enviado com sucesso! Verifique seu email.');
      })
      .catch((error) => {
        console.error('Erro ao enviar o formulário:', error);
        alert('Ocorreu um erro ao enviar o formulário. Tente novamente mais tarde.');
      });
  };

  return (
    <div className="container">
      <h2>Formulário de Venda de Hardwares</h2>
      <form onSubmit={handleSubmit}>
        <div className="question">
          <label htmlFor="companyName">Informe o nome da Empresa:</label>
          <input
            type="text"
            id="companyName"
            name="companyName"
            value={formData.companyName}
            onChange={handleChange}
            required
          />
        </div>
        <div className="question">
          <label htmlFor="cnpj">Informe o CNPJ do cliente:</label>
          <input
            type="text"
            id="cnpj"
            name="cnpj"
            value={formData.cnpj}
            onChange={handleChange}
            required
            pattern="[0-9]{2}\.[0-9]{3}\.[0-9]{3}/[0-9]{4}-[0-9]{2}"
            placeholder="00.394.460/0058-87"
          />
        </div>
        <div className="question">
          <label htmlFor="vendorName">Informe o nome do vendedor:</label>
          <input
            type="text"
            id="vendorName"
            name="vendorName"
            value={formData.vendorName}
            onChange={handleChange}
            required
          />
        </div>
        <div className="question">
          <label htmlFor="totalValue">Informe o valor total vendido:</label>
          <input
            type="number"
            id="totalValue"
            name="totalValue"
            step="0.01"
            value={formData.totalValue}
            onChange={handleChange}
            required
          />
        </div>
        <div className="question">
          <label htmlFor="observations">Campo livre para observações:</label>
          <textarea
            id="observations"
            name="observations"
            value={formData.observations}
            onChange={handleChange}
          ></textarea>
        </div>
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
};

export default HardwareForm;
