import React, { useState } from 'react';
import axios from 'axios';
import ProjectForm from './components/ProjectForm';
import HardwareForm from './components/HardwareForm';
import PocForm from './components/PocForm';
import FormDataDisplay from './components/FormDataDisplay';
import './components/assets/css/App.css';
import LogoInovacode from './components/assets/logo/logo2.0.png';

function App() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    requestType: '',
    companyName: '',
    cnpj: '',
    vendorName: '',
    totalValue: '',
    observations: '',
    proposalAttachment: null, // Holds the selected file for proposal attachment
    clientName: '',
    salesRepresentative: '',
    sponsor: '',
    requestedSolution: '',
    pocDate: '',
    applicationContext: '',
    chargeClient: '',
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });

    // Specific handling for requestType
    if (name === 'requestType') {
      // Reset additional fields based on requestType
      setFormData((prevFormData) => {
        if (value === 'Projeto Vendido' || value === 'Projeto Piloto' || value === 'PRO Bono') {
          return { ...prevFormData, companyName: '', cnpj: '', vendorName: '', totalValue: '', observations: '' };
        } else if (value === 'Venda somente de hardwares') {
          return { ...prevFormData, companyName: '', cnpj: '', vendorName: '', totalValue: '', observations: '' };
        } else if (value === 'Prova de conceito (POC)') {
          return { ...prevFormData, clientName: '', cnpj: '', salesRepresentative: '', sponsor: '', requestedSolution: '', pocDate: '', applicationContext: '', chargeClient: '' };
        } else {
          return prevFormData;
        }
      });
    }
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setFormData({ ...formData, proposalAttachment: file });
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();

    const finalFormData = new FormData();
    for (const key in formData) {
      finalFormData.append(key, formData[key]);
    }

    axios
      .post('http://localhost:5000/api/enviar-email', finalFormData)
      .then((response) => {
        console.log('Resposta do backend:', response.data);
        alert('Formulário enviado com sucesso! Verifique seu email.');
      })
      .catch((error) => {
        console.error('Erro ao enviar o formulário:', error);
        alert('Ocorreu um erro ao enviar o formulário. Tente novamente mais tarde.');
      });
  };

  let formToDisplay;
  if (formData.requestType === 'Projeto Vendido' || formData.requestType === 'Projeto Piloto' || formData.requestType === 'PRO Bono') {
    formToDisplay = <ProjectForm formData={formData} handleChange={handleChange} handleFileChange={handleFileChange} onSubmit={handleFormSubmit} />;
  } else if (formData.requestType === 'Venda somente de hardwares') {
    formToDisplay = <HardwareForm formData={formData} handleChange={handleChange} handleFileChange={handleFileChange} onSubmit={handleFormSubmit} />;
  } else if (formData.requestType === 'Prova de conceito (POC)') {
    formToDisplay = <PocForm formData={formData} handleChange={handleChange} handleFileChange={handleFileChange} onSubmit={handleFormSubmit} />;
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={LogoInovacode} alt="Logo Inovacode" />
      </header>
      <div className="App-body">
        <div className="App-form">{formToDisplay}</div>
        <div className="App-data">
          <FormDataDisplay formData={formData} />
        </div>
      </div>
    </div>
  );
}

export default App;
